			r.setQueryParameter("sysparm_query", search_query);
			var response = r.execute();
			var responseBody = response.getBody();
			gr.setValue('schemeCode', 'schemeCode');
			gr.setValue('schemeName', 'schemeName');
			for(var m in mutualFunds) {
				if(m < 10) {
					gs.info(mutualFunds[m].schemeCode + " -> " + mutualFunds[m].schemeName );
					gr.insert(m);
				}
			}
			gr.execute();
			var httpStatus = response.getStatusCode();
		}
		catch(ex) {
 			var message = ex.message;
		}
	},
